package operadoresRelacionales;

public class OperadoresRelacinales {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//operadores relacionales
		// >, < , >=, <=, !=  direfencia, ! negacion, == igualdad
		// tipo = boolean
		boolean dato = 5>=5;
		System.out.println(dato);
		boolean diferencia = 5 !=6;
		System.out.println(diferencia);
		boolean negacion = !true;
		System.out.println(negacion);
		boolean igualadad = 5==5;
		System.out.println(igualadad);
		
	}

}
