package tipoDeDatos;

public class tipoDatos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//tipo de datos
		//tipo de datos primtivos
		/*
		 * byte -- 8bit
		 * short -- 16bit
		 * int -- 32bit
		 * long -- 64bit
		 * float -- 32bit
		 * double -- 64bit
		 * boolean -- ?
		 * char -- 16bit
		 */
		
		/*
		 * numerico(entero):
		 * byte -- 8bit
		 * short -- 16bit
		 * int -- 32bit
		 * long -- 64bit
		 * */
		byte nu1 = 10;
		short nu2 = 10000;
		int nu3 = 1000000;
		long nu4 =1000000000L;
		
		
		/*
		 * numeros con decimales:
		 * float -- 32bit
		 * double -- 64bit
		 * */
		float des1 = 3.1415f;
		double des2 = 100000.0000000;
		
		/*
		 * texto:
		 * char -- 16bit
		 * String (no permetece a tipo de datos primivos)
		 * */
		char iniNombre = 'R';
		String nombreCompleto = "rodrigo";
		
		
		/*
		 * logico:
		 * boolean -- ?
		 * */
		boolean activo = true;
		boolean inactivo = false;
		
		//ejemplo:
		
	}

}
